mapper.select:
SELECT O.WORDID, WORDWORD, WORDFURIGANA, WORDMEAN, WORDLEVEL, EXFRONTJP, EXBACKJP, EXFRONTKR, EXBACKKR, WRONGNUM
FROM (
	SELECT W.WORDID, WORDWORD, WORDFURIGANA, WORDMEAN, WORDLEVEL, WRONGNUM
	FROM T_WORD W JOIN T_WRONG R ON W.WORDID = R.WORDID
	WHERE USERID = #{userId}
) O JOIN T_EX E ON O.WORDID = E.WORDID

SELECT WRONGID, WORDID, USERID, WRONGNUM, WRONGREGISTERDATE, WRONGUPDATEDATE
FROM T_WRONG
WHERE USERID = #{userId} AND WORDID = #{wordId}

mapper.update:
UPDATE T_WRONG
SET WRONGNUM = #{wrongNum - 1}, WRONGUPDATEDATE=SYSDATE
WHERE WRONGID = #{wrongId}
