mapper.select:
SELECT USERID, USERPW, USERNAME, USERPHONE, USEREMAIL, USERADMIN, USERREGISTERDATE, USERUPDATEDATE
FROM T_USER
WHERE USERID = #{userId}

SELECT COUNT(USERID)
FROM T_USER
WHERE USERID = #{userId}

mapper.update:
UPDATE T_USER
SET USERNAME= #{userName}, USERPHONE= #{userPhone}, USEREMAIL= #{userEmail}, USERPROFILE = #{userProfile}, USERUPDATEDATE=SYSDATE
WHERE USERID= #{userId}
