mapper.update:
UPDATE T_BOARD SET 
BOARDTITLE=#{boardTitle}, BOARDMAIN=#{boardMain} 
WHERE BOARDID=#{boardId}

UPDATE T_BOARD SET 
BOARDVIEWS=#{boardViews}
WHERE BOARDID=#{boardId}

mapper.select:
SELECT BOARDID, BOARDTITLE, b.USERID, BOARDMAIN, BOARDNOTICE, BOARDVIEWS, BOARDREGISTERDATE, BOARDUPDATEDATE, USERPROFILE
FROM T_BOARD b JOIN T_USER u ON b.USERID = u.USERID
WHERE BOARDID = #{boardId}
